/**
 * Matrix stored as an array in column-major order:
 *
 * ```
 * [
 *     m11, m21, m31, m41,
 *     m12, m22, m32, m42,
 *     m13, m23, m33, m43,
 *     m14, m24, m34, m44
 * ],
 * ```
 *
 * where mij - matrix element in the i-th row and j-th column.
 */
interface Matrix4 {
    [i: number]: number;
    readonly length: number;
}
export { Matrix4 };
